import React from "react";
import { Button } from "./Button";

export class RandomColor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { color: [200, 200, 140] };
  }

  chooseColor() {
    const random = [];
    for (let i = 0; i < 3; i++) {
      random.push(Math.floor(Math.random() * 256));
    }
    return random;
  }

  formatColor(ary) {
    return "rgb(" + ary.join(",") + ")";
  }

  applyColor() {
    const color = this.formatColor(this.chooseColor());
    document.body.style.background = color;
  }

  handleClick() {
    this.setState({ color: this.chooseColor });
  }

  render() {
    return (
      <div id="randomColor">
        <p> </p>
        <Button onClick={this.appyColor} />
      </div>
    );
  }
}
