import React from "react";

export const Task = ({ allTasks, handleDelete, handleComplete }) => {
  return allTasks.map((task, index) => (
    <div className="task" key={task.index}>
      <h3>{task.title}</h3>
      <p>{task.decription}</p>
      <div className="buttons">
        <button className="delete" onClick={() => handleDelete(task)}>
          Delete
        </button>
        <button className="complete" onClick={() => handleComplete(task)}>
          Complete
        </button>
      </div>
    </div>
  ));
};
