import React from "react";

export const NewTask = ({ newTask, handleChange, handleSubmit }) => {
  return (
    <form onSubmit={handleSubmit}>
      <input
        name="name"
        onChange={handleChange}
        placeholder="title"
        type="text"
        id="name"
        //name="title"
        //value={newTask.title || ""}
        required
      />

      <input
        name="description"
        placeholder="description"
        type="text"
        id="description"
        required
      />

      <button type="submit">Add</button>
    </form>
  );
};
