import { useState } from "react";
import { Header } from "../textDisplay/Header";
import { Counter } from "./Counter";
import { NewTask } from "./NewTask";
import { TaskList } from "./TaskList";
import Grid from "@mui/material/Grid";
import * as React from "react";

export const ToDoBoard = () => {
  const [newTask, setNewTask] = useState({});
  const handleChange = ({ target }) => {
    const { name, value } = target;
    setNewTask((prev) => ({ ...prev, [name]: value }));
  };

  const [allTasks, setAllTasks] = useState([]);
  const handleSubmit = (event) => {
    event.preventDefault();

    if (newTask.title !== "") {
      setAllTasks((prev) => [newTask, ...prev]);
      setNewTask({});
    }
  };

  const handleDelete = (taskToRemove) => {
    setAllTasks((prev) =>
      prev.filter((task) => task.title !== taskToRemove.title)
    );
  };

  const [completeTasks, setCompleteTasks] = useState([]);
  const handleComplete = (taskCompleted) => {
    setCompleteTasks((prev) => [...prev, taskCompleted]);
  };

  const handleView = () => {
    let tasksContainer = document.createElement("div");
    tasksContainer.id = "tasksContainer";
    let completedTasks = document.createElement("h1");

    completedTasks.innerHTML = "Completed Tasks";
    document.getElementById("App").appendChild(tasksContainer);

    document.getElementById("App").lastChild.appendChild(completedTasks);
    completeTasks.map((task) => {
      let eachTask = document.createElement("p");
      eachTask.innerHTML = task.title;

      document.getElementById("tasksContainer").appendChild(eachTask);
    });
  };

  return (
    <Grid
      sx={{
        width: 1 / 3,
        mx: "auto",
        mt: 6,
        py: 2,
        textAlign: "center",
      }}
      id="todoBoard"
    >
      <Header handleView={handleView} completeTasks={completeTasks} />

      <NewTask
        newTask={newTask}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />

      <TaskList
        allTasks={allTasks}
        handleDelete={handleDelete}
        handleComplete={handleComplete}
      />

      <Counter
        allTasks={allTasks}
        completeTasks={completeTasks}
        handleView={handleView}
      />
    </Grid>
  );
};
