import * as React from "react";
import Badge from "@mui/material/Badge";

export const Header = ({ allTasks, completeTasks }) => {
  return (
    <div sx={{ display: "flex", flexDirection: "row" }}>
      <h1>TO DO LIST</h1>
      <Badge
        color="secondary"
        badgeContent={allTasks.length - completeTasks.length}
      ></Badge>
    </div>
  );
};
