import * as React from "react";
import Badge from "@mui/material/Badge";

export const Header = ({ allTasks, handleView }) => {
  return (
    <div>
      <h1 sx={{ display: "inline" }}>TO DO LIST</h1>
      <Badge
        color="secondary"
        badgeContent={allTasks.length}
        onClick={handleView} //查看已完成任务
      ></Badge>
    </div>
  );
};
