import * as React from "react";
import Badge from "@mui/material/Badge";

export const Counter = ({ completeTasks, handleView }) => {
  return (
    <div>
      <Badge className="counter" onClick={handleView}>
        <h1 className="countNum">{completeTasks.length}</h1>
      </Badge>

      <Badge color="secondary" badgeContent={0} showZero></Badge>
    </div>
  );
};
