import React from "react";

export const NewTask = ({ newTask, handleChange, handleSubmit }) => {
  return (
    <form onSubmit={handleSubmit}>
      <input
        value={newTask}
        type="text"
        onChange={handleChange}
        placeholder="things to do..."
        height="40"
      />
      <button type="submit">Add</button>
    </form>
  );
};
